import { Platform } from 'react-native';

type StyleObject = {
  [key: string]: any;
};

export const createPlatformStyle = (styles: {
  common: StyleObject;
  web?: StyleObject;
  native?: StyleObject;
}): StyleObject => {
  const { common, web, native } = styles;

  if (Platform.OS === 'web' && web) {
    return {
      ...common,
      ...web,
    };
  }

  if (Platform.OS !== 'web' && native) {
    return {
      ...common,
      ...native,
    };
  }

  return common;
};

export const createShadowStyle = (elevation: number = 3) => {
  return createPlatformStyle({
    common: {},
    web: {
      boxShadow: `0 ${elevation}px ${elevation * 2}px rgba(0, 0, 0, 0.1)`,
    },
    native: {
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: elevation,
      },
      shadowOpacity: 0.1,
      shadowRadius: elevation,
      elevation,
    },
  });
};
